/* We need jQuery for this one */
SVG.extend(SVG.Use, {

    element:function(element, file){

        if(file && !this.doc().defs().select('#' + element).length()){
            var $node = $(this.doc().defs().node);
            $.get(file, function(svg){
                $(svg).find('#' + element).appendTo($node);
            })
        }
    
        return this.attr('href', '#' + element, SVG.xlink)
    
    }

} )